import React from 'react';
import PRP from './Image.jpeg'
import { useState, useEffect } from 'react';
import { check_token_validity, get_token } from './imports';
import { useNavigate } from 'react-router-dom';

function PDP() {
  const navigate = useNavigate();
  const token = get_token();

  const [user, setUser] = useState([]);

  const fetchOptions = {
    method: "POST",
    headers: {
      "Content-Type": "application/json",
      "Authorization": `Bearer ${token}`,
    },
    body: JSON.stringify({}),
  };

  useEffect(() => {
    check_token_validity(token)
      .then(result => {
        if ( token == undefined || result === 'expired' )
        {
            navigate('/');      
        }
      })
      .catch(error => {
        console.error(error);
      });

    fetch(`http://localhost:8000/get_user/`, fetchOptions)
    .then((response) => response.json())
    .then((data) => setUser(data));

  }, []);

    return (
      <div className="max-w-md mx-auto mt-8 p-6  rounded-md shadow-md text-center font-b">
        <h1 className="text-4xl font-bold text-white">PERSONALISATION de votre profil</h1>
        <div> <img className='mt-8 rounded-full w-48 ml-24 ' src={require('./Image.jpeg')} alt ="PRP image"/></div>
        <form className="mt-8">
          <div className="mb-4">
            <label htmlFor="email" className="block text-white text-2xl font-bold mb-2"> Nom du profil </label>
            <input type="text" id="Profile" name="email" value={user['username']} className="w-full bg-transparent text-white rounded-full border-2 border-cyan-500 p-2 focus:outline-none"/>
            <div className="mt-8">
        <h1 className="mr-2 text-white text-2xl font-bold">SEXE</h1>
        <ul className="mt-4 grid grid-cols-3">
        <li>
        <input className="bg-cyan-300 w-full hidden peer" type="checkbox" id="francais" value="francais" />
        <label htmlFor="francais" className="text-xl border-2 border-cyan-500 py-1 px-9 text-cyan-500 rounded-full hover:border-cyan-700 hover:text-cyan-700 peer-checked:bg-cyan-500 peer-checked:text-white"> MALE </label>
        </li>
        <li>
        <input className="bg-cyan-300 w-full hidden peer" type="checkbox" id="arabe" value="arabe" />
        <label htmlFor="arabe" className="text-xl border-2 border-cyan-500 py-1 px-9 text-cyan-500 rounded-full hover:border-cyan-700 hover:text-cyan-700 peer-checked:bg-cyan-500 peer-checked:text-white">FEMALE</label>
        </li>
        <li>
        </li>
        </ul>
        </div>
        <h1 className="w-full mt-12 text-4xl font-bold text-white">CHANGER DE MOT DE PASSE</h1>
        <div className="mt-8 grid grid-cols-3 max-sm:grid-cols-2 gap-3">


      </div>
          </div>
          <input className="mt-12 w-full rounded-full bg-cyan-500 py-2 text-white hover:bg-cyan-600 transition duration-300 text-xl focus:outline-none" type="submit" value="continuer"/>
          </form>
          </div>
    );
};

export default PDP;
