import React from 'react';
import PRP from './Image.jpeg';
import { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

function PDP() {
    const navigate = useNavigate();

    function check_token()
    {
      return document.cookie.split('; ').filter(row => row.startsWith('token=')).map(c=>c.split('=')[1])[0];
    }

    useEffect(() => {
      const token = check_token();

      if ( token == undefined )
      {
          navigate('/');      
      }
      else
      {
          alert(token);
      }
    }, []);

    return (
      <div className="max-w-md mx-auto mt-8 p-6  rounded-md shadow-md text-center font-b">
        <h1 className="mt-12 text-4xl font-bold text-white">PERSONALISATION de votre profil</h1>
        <div> <img className='mt-8 rounded-full w-48 ml-24 ' src={require('./Image.jpeg')} alt ="PRP image"/></div>
        <form className="mt-8">
          <div className="mb-4">
            <label htmlFor="email" className="block text-white text-2xl font-bold mb-2"> Nom du profil </label>
            <input type="text" id="Profile" name="email" className="w-full bg-transparent text-white rounded-full border-2 border-cyan-500 p-2 focus:outline-none"/>
            <div className="flex items-center mb-4 mt-8">
          <label className="mr-2 text-white text-2xl font-bold">LANGUES</label><br />
          <input type="radio" id="male" name="gender" className="hidden" />
          <label htmlFor="FRANCAIS" className="text-xl cursor-pointer border-2 border-cyan-500 py-1 px-8 text-cyan-500 rounded-full selected:bg-cyan-500 mr-2"> FRANCAIS </label>
          <input type="radio" id="ARABE" name="gender" className="hidden" />
          <label htmlFor="female" className="text-xl cursor-pointer border-2 border-cyan-500 py-1 px-8 text-cyan-500 rounded-full selected:bg-cyan-500"> ARABE </label>
          <input type="radio" id="ANGLAIS" name="gender" className="hidden" />
          <label htmlFor="female" className="text-xl cursor-pointer border-2 border-cyan-500 py-1 px-8 text-cyan-500 rounded-full selected:bg-cyan-500"> ANGLAIS </label>
        </div>
        <h1 className="w-f ull mt-12 text-4xl font-bold text-white">CHOISSISER 3 FILMS OU SERIES FAVORITES</h1>
          </div>
          </form>
          </div>
    );
};

export default PDP;
