import React from 'react';
import { useState, useEffect } from 'react';
import { check_token_validity, get_token } from './imports';
import { useNavigate } from 'react-router-dom';

const MP = () => {
  const navigate = useNavigate();
  const token = get_token();

  const state = check_token_validity(token);

  alert(state.state);

  if ( token == undefined || check_token_validity(token) == 'expired' )
  {
      navigate('/');      
  }

  const [forYou, setForYou] = useState([]);
  const [SearchMovies, setSearchMovies] = useState([]);
  const [liked, setLiked] = useState([]);
  const [action, setAction] = useState([]);
  const [adventure, setAdventure] = useState([]);
  const [comedy, setComedy] = useState([]);

  const fetchOptions = {
    method: "POST",
    headers: {
      "Content-Type": "application/json",
      "Authorization": `Bearer ${token}`,
    },
    body: JSON.stringify({}),
  };

  useEffect(() => {
      fetch(`http://localhost:8000/foryou/`, fetchOptions)
      .then((response) => response.json())
      .then((data) => setForYou(data));

      fetch(`https://api.themoviedb.org/3/search/movie?query=irishman&api_key=2da959128bf01c42309d7a6b378a64df&language=en-US&page=1`)
      .then((response) => response.json())
      .then((data) => setSearchMovies(data.results));

      fetch(`http://localhost:8000/liked/`, fetchOptions)
      .then((response) => response.json())
      .then((data) => setLiked(data));

      fetch(`http://localhost:8000/discover?genre=Action`)
      .then((response) => response.json())
      .then((data) => setAction(data));

      fetch(`http://localhost:8000/discover?genre=Adventure`)
      .then((response) => response.json())
      .then((data) => setAdventure(data));

      fetch(`http://localhost:8000/discover?genre=Comedy`)
      .then((response) => response.json())
      .then((data) => setComedy(data));

  }, []);

  return (
        <div className="rounded-md shadow-md text-left font-b">
        <nav class="flex items-center justify-between flex-wrap p-6">
        <div class="flex items-center flex-shrink-0 text-white mr-6">
        <div> <img className='rounded-full w-12' src={require('./Image.jpeg')} alt ="PRP image"/></div>
          <span class="ml-4 text-3xl tracking-tight max-sm:hidden lg:shown ">OMAR</span>
          <button className="max-sm:ml-4 ml-8 w-24 rounded-full border-2 border-gray-700 p-1 text-gray-500 hover:text-cyan-300 hover:border-cyan-300  transition duration-300 text-xl focus:outline-none"> SERIES </button>
          <button className="ml-2 w-24 rounded-full border-2 border-gray-700 p-1 text-gray-500 hover:text-cyan-300 hover:border-cyan-300  transition duration-300 text-xl focus:outline-none"> FILMS </button>
          <button id="dropdown-button" data-dropdown-toggle="dropdown" className="flex-shrink-0 z-10 inline-flex items-center py-1 px-4 ml-2 w-32 rounded-full border-2 border-gray-700 p-1 text-gray-500 hover:text-cyan-300 hover:border-cyan-300  transition duration-300 text-xl focus:outline-none" type="button">CATEGORIES <svg class="w-2.5 h-2.5 ms-2.5" aria-hidden="true" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 10 6">
    <path stroke="currentColor" stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="m1 1 4 4 4-4"/>
  </svg></button>
        </div>

        <div class="block">
        <form>   
        <label for="default-search" class="mb-2 text-sm font-medium text-gray-900 sr-only dark:text-white">Search</label>
        <div class="relative">
        <input type="search" id="default-search" className="lg:w-80 block max-sm:hidden w-full bg-transparent text-gray-700 focus:text-white rounded-full border-2 border-gray-700 p-2 focus:outline-none focus:border-cyan-300" placeholder="RECHERCHER" required/>
        <button type="submit" class="text-white absolute max-sm:bottom-0 end-3 bottom-3.5">
            <svg class="w-4 h-4 max-sm:w-6 max-sm:h-6 text-gray-500 dark:text-gray-400" aria-hidden="true" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 20 20">
